web3 = Web3(Web3.HTTPProvider("https://rinkeby.infura.io/v3/3f0fa5d9c4064d6e8427efac291d66df"))
if NETWORK == "rinkeby":
    web3.middleware_stack.inject(geth_poa_middleware, layer=0)

HYDRO_CONTRACT = web3.eth.contract(address=HYDRO_ADDRESS[NETWORK], abi=HYDRO_ABI)
UNISWAP_FACTORY_CONTRACT = web3.eth.contract(address=UNISWAP_FACTORY_ADDRESS[NETWORK], abi=UNISWAP_FACTORY_ABI)
UNISWAP_HYDRO_EXCHANGE_ADDRESS = UNISWAP_FACTORY_CONTRACT.functions.getExchange(HYDRO_ADDRESS[NETWORK]).call()
UNISWAP_HYDRO_EXCHANGE_CONTRACT = web3.eth.contract(address=UNISWAP_HYDRO_EXCHANGE_ADDRESS, abi=UNISWAP_EXCHANGE_ABI)


def get_HYDRO_supply():
    """Get total available HYDRO supply."""
    return HYDRO_CONTRACT.functions.balanceOf(UNISWAP_HYDRO_EXCHANGE_ADDRESS).call() / 1e18


def get_ETH_HYDRO_price_uniswap(input_reserve=None, output_reserve=None):
    """Get ETH price of HYDRO (replicates getOutputPrice)."""
    output_amount = int(f"1{'0' * 18}")
    input_reserve = input_reserve if input_reserve is not None else web3.eth.getBalance(UNISWAP_HYDRO_EXCHANGE_ADDRESS)  # eth reserve
    output_reserve = output_reserve if output_reserve is not None else HYDRO_CONTRACT.functions.balanceOf(UNISWAP_HYDRO_EXCHANGE_ADDRESS).call()  # hydro reserve

    numerator = input_reserve * output_amount * 1000
    denominator = (output_reserve - output_amount) * 997

    return (numerator / denominator + 1) / 1e18


def market_order_uniswap(hydro_volume, order_type):
    """Get the new price and cost/revenue for a trade volume."""
    converted_volume = int(f"{int(hydro_volume)}{'0' * 18}")
    if type == "sell_hydro":
        eth_total = UNISWAP_HYDRO_EXCHANGE_CONTRACT.functions.getTokenToEthInputPrice(converted_volume).call()
    else:
        assert get_HYDRO_supply() >= hydro_volume
        eth_total = UNISWAP_HYDRO_EXCHANGE_CONTRACT.functions.getEthToTokenOutputPrice(converted_volume).call()

    # compute counterfactual price
    input_reserve = web3.eth.getBalance(UNISWAP_HYDRO_EXCHANGE_ADDRESS)
    input_reserve_counterfactual = input_reserve - eth_total if type == "sell_hydro" else input_reserve + eth_total

    output_reserve = HYDRO_CONTRACT.functions.balanceOf(UNISWAP_HYDRO_EXCHANGE_ADDRESS).call()
    output_reserve_counterfactual = output_reserve + converted_volume if type == "sell_hydro" else output_reserve - converted_volume

    new_rate = get_ETH_HYDRO_price_uniswap(input_reserve_counterfactual, output_reserve_counterfactual)

    return [eth_total / 1e18, new_rate]
